var upChars = directions.ToCharArray().Count(x => x.Equals('('));
var downChars = directions.ToCharArray().Count(x => x.Equals(')'));

var difference = directions.Length - upChars - downChars;
var floor = upChars - downChars;

Console.WriteLine($"The difference from counting? {difference}");
