var basefloor = 0;
var finalfloor = basefloor;
var iDonno = 0;
var count = 0;
var basementFound = false;

foreach (var item in directions)
{
    if (item == '(')
        finalfloor++;
    else if (item == ')')
        finalfloor--;
    else
    {
        Console.WriteLine("I donno");
        iDonno++;
    }
    count++;
    if (finalfloor < 0 && !basementFound)
    {
        Console.WriteLine($"The pos that it becomes basement{finalfloor}: {count}");
        basementFound = true;
    }
}

Console.WriteLine($"Final floor by looping: {finalfloor}");
